import React, { ChangeEvent, FC, useState } from 'react';
import styled from 'styled-components';
import { IState } from '../../reducers';
import { useSelector } from 'react-redux';
import { ISingleUser } from '../../entities/users';
import { ISingleTodos } from '../../entities/todos';
import { ISinglePhoto } from '../../entities/photos';

const SingleEntityWrapper = styled.div`

`;
const SingleEntityBox = styled.div`
    height:150px;
    border: 2px solid ;
    width:300px;
    margin-left:18px;
    margin-top:20px;
    float:left;
`;
const SingleEntityPicture = styled.img`
    height:150px;
    float:left;
    width:150px;
`;
const SingleEntityName = styled.div`
font-size:25px;
margin-top:20px;
margin-bottom:20px;
`;

const SingleEntityAddress = styled.div``;
interface ISingleEntity{
    name: string;
    address: string;
    url: string;
    list:boolean;
}

export const SingleEntity: FC<ISingleEntity> = (props) => {
    return (
        
        <SingleEntityWrapper>
            <SingleEntityBox style={{display:`${props.list?'grid':'grid'}` ,}}>
            {/* {width:`${props.list?'1200px':'300px'}` */}
            {/* display: grid;
  grid-template-columns: repeat(5, 1fr);
  grid-auto-rows: minmax(100px, auto); */}

                <SingleEntityPicture src={props.url}/>
                <SingleEntityName>
                    {props.name}
                </SingleEntityName>
                <SingleEntityAddress>
                    {props.address}
                </SingleEntityAddress>
            </SingleEntityBox>
        </SingleEntityWrapper>
    );
};
