import React, {FC} from 'react';
import styled from 'styled-components';
import { useSelector } from 'react-redux'; // wyciąga nam dane z reducerów
import { IState } from '../../reducers';
import { IUsersReducer } from '../../reducers/usersReducers';
import { IPhotosReducers } from '../../reducers/photosReducers';
import { IPostsReducers } from '../../reducers/postsReducers';
import { Link } from 'react-router-dom';

// 4 komentarze z postów w połączneiu w useramy i phots
// obrazki z photos, imie i nazwosko osoby która napisała post oraz zdjęcia, data na sztywno, opis z body w postach

const StyledWrapper = styled.div`
height: 350px ;
width: 1200px ;
background: white;
border: 5px;
margin-top: 20px;
margin-left: 100px;
border-radius: 5px;
padding: 0;
border: 1px solid lightgray;
`;

const BigImageContainer = styled.div`
width: 33.3%;
`;


const OverflowingTextFeild = styled.p`
text-overflow: ellipsis;
overflow: hidden;
font-size: 17px;
height: 36px;
white-space: no-wrap;
font-weight: 500;
`;

const AbsoluteContainer = styled.blockquote`
    margin-top:60px;
    width: 300px;
    position: absolute;
    padding-left: 20px;
    color: white;
    top: 320px;
    z-index: 1000;
`;
const InnerWrapper = styled.div`
`;
const Wrapper = styled.div`
`;
const Title = styled.div`
`;
const BlockContainer = styled.div``;

const StyledLink = styled(Link)`
    text-decoration: none;
`;
export const LatestPublications: FC = () => {
    const { usersList, photosList, postsList } = useSelector<IState, IUsersReducer & IPhotosReducers &
        IPostsReducers>(globalState => ({
        ...globalState.users,
        ...globalState.photos,
        ...globalState.posts,
        }));
    
        if(usersList)
        return (
        <StyledWrapper>
            <BigImageContainer>
           <img src={photosList[0]?photosList[0].url:""} alt=""/> 
                <AbsoluteContainer>
                    <OverflowingTextFeild>
       
                    </OverflowingTextFeild>
                    <InnerWrapper>
                        <h4>7 jan. 2020</h4>
          
                        <h4>{usersList?.[0]?.name}</h4>
                    </InnerWrapper>
                </AbsoluteContainer>
            </BigImageContainer>
            <BlockContainer>
                <Title>Latest publications</Title>
                <Wrapper>
          
                    <BlockContainer>
                        
                        <InnerWrapper>
                        <h4>7 jan. 2020</h4>
                 
                        <h4>{usersList?.[0]?.name}</h4>
                    </InnerWrapper>
                </BlockContainer>
            </Wrapper>
            <Wrapper>
    
                <BlockContainer>
         
                    <InnerWrapper>
                        <h4>7 jan. 2020</h4>
            
                        <h4>{usersList?.[0]?.name}</h4>
                    </InnerWrapper>
                </BlockContainer>
            </Wrapper>
            <Wrapper>
  
                <BlockContainer>
           
                    <InnerWrapper>
                        <h4>7 jan. 2020</h4>
            
                        <h4>{usersList?.[0]?.name}</h4>
                    </InnerWrapper>
                </BlockContainer>
            </Wrapper>
            <StyledLink to="/test-site">
     
            </StyledLink>
        </BlockContainer>
    </StyledWrapper>
    ) 
    return null}
