import  { FC } from 'React';
import styled from 'styled-components';
import { Wrapper } from '../../styledHelpers/Components';
import { Colors } from '../../styledHelpers/colors'
import useDropdown from 'react-dropdown-hook';
import Link from '../common/Link' ;
import { ExpandedMenu } from '../TopBar/ExpendedMenu'

const InnerWrapper = styled.div`
width:100%;
background: ${Colors.white};
`;

const RightIcons = styled.div`
display:flex;
`;
const LeftIcons = styled.div`
float: left;
`;
const SearchBar = styled.div`

`;
const Home = styled.label`
margin-left:30px;
margin-bottom:5px;
padding-bottom:10px;
`;

const InputWrapper = styled.div`
display:flex;
justify-content: space-between;
align-items:center;
margin-top:5px;
margin-bottom:5px;

`;
const Logo= styled.img`
width:10%;
height:30px;
margin-left:10px;
margin-right:10px;
`;
const HomeIcon = styled.img`
margin-bottom:4px;
`;
const HouseIcon = styled.img`
margin-right:10px;
`;

const CommentsIcon = styled.img`
margin-right:10px;
`;
const BellIcon = styled.img`
margin-right:10px;
`;
const SearchIcon = styled.img`
margin-left:15px;
margin-bottom:-4px;
`;

const Input = styled.input`
width: 300px;
margin-left:-250px;
`;
const Menu = styled.div`
margin-right:700px;
padding-right:800px;
`;
const ArrowDownIcon = styled.img`

`;



export const TopBar: FC = () => {
    const [wrapperRef, dropdownOpen, toggleDropdown, closeDropdown] = useDropdown();
    return (
        <Wrapper>
            <InnerWrapper>

                <InputWrapper>
                <LeftIcons>
                <Logo src="./media/icons/logo.png"/> 
                <HomeIcon src="./media/icons/house2.png" />
                <Home >HOME</Home>
               
                </LeftIcons>
                <Menu ref={wrapperRef}>
                        <div onClick={toggleDropdown}>
                        <ArrowDownIcon src="./media/icons/arrow-down.png"  alt=""/>
                        </div>
                            {dropdownOpen &&
                                <>
                                    <ExpandedMenu>
                                        
                                    </ExpandedMenu>
                                </> 
                            }
                    </Menu>
                <div onClick={closeDropdown}></div>
                <SearchBar>
                        <Input type="szukaj" placeholder="Hello there"  />
                         <SearchIcon src ="./media/icons/search.png"  alt="" />
                </SearchBar>
                <RightIcons>
                    <HouseIcon src="./media/icons/house.png" alt=""/>
                    <CommentsIcon src="./media/icons/comments.png" alt="" />
                    <BellIcon src="./media/icons/bell.png" alt=""/>
                </RightIcons>
                </InputWrapper>
            </InnerWrapper>
        </Wrapper>
    );
};
